import uvicornimport predict_sentimentfrom fastapi import FastAPIfrom pydantic import BaseModelfrom typing import Optionalclass Item(BaseModel):    name: strapp = FastAPI()@app.post("/items")async def create_item(item: Item):    prediction =float(predict_sentiment.sentiment_prediction(item.name))    return(prediction)# 3. Expose the prediction functionality, make a prediction from the passed#    JSON data and return the predicted flower species with the confidence@app.get('/test')def test():        return 'tests'# 4. Run the API with uvicorn#    Will run on http://127.0.0.1:8000if __name__ == '__main__':    uvicorn.run(app, host='127.0.0.1', port=8000)